mapper.select:
SELECT d.title, d.main_deal_srl, d.`deal_status`, d.`delivery_policy`, d.`delivery_amount`, d.`delivery_if_amount`, d.`category1`, d.`category2`, d.`category3`, d.`category4`, o.`status`, o.deal_option_srl, o.amount, o.depth1, o.depth2, o.depth3 FROM deal AS d LEFT JOIN deal_option AS o ON d.main_deal_srl = o.main_deal_srl WHERE o.deal_option_srl IN
#{orderView.dealOptionSrl}
ORDER BY main_deal_srl DESC
